const MessageInput = ({
    isDisabled,
    isLoading,
    inputText,
    handleKeyDown,
    handleSendMessage,
    setInputText,
  }) => {

    const [selected, setSelected] = useState(false);
    const [data, setData] = useContext(DataContext)
    const placeholder = isDisabled
      ? "Please enter your OpenAI API key above."
      : "Tell me about a historical event you want to learn more about.";


    const handleCTA = () => {
      handleSendMessage(inputText, selected);
    };

    const handleKeyDownCTA = (e) => {
      if (e.key === "Enter") {
        handleSendMessage(inputText);
      }
    };



    return (
      <>

      <div style="marginBottom: '100px'">
        <TextArea
        style="height: '35%', width: '100%'"
          placeholder={placeholder}
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyDown={handleKeyDownCTA}
          disabled="isDisabled || isLoading"
        />
        <Button
          auto
          flat
          color=" 'primary' || 'warning'"
          style="marginLeft: '10px', width: '98%'"
          onClick={handleCTA}
          disabled="isDisabled || isLoading"
          css={ 
            '&:hover': {
              transform: 'translateY(-5px)',
              '&:after': {
                transform: 'scaleX(1.5) scaleY(1.6)',
                opacity: 0
              }
            },
            '&:active': {
              transform: 'translateY(-2px)'
            }
          }
        >
           ğŸ–‹
        </Button>

      </div>

      </>
    )};

  export default MessageInput;
